INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (45, 'historique_desc', 'qui_sommes_nous', 'MAD\'AROM est une entreprise artisanale spécialisée dans la production de plantes thérapeutiques, médicinales et aromatiques très recherchées à Madagascar. Créée en 2020, l\'entreprise s\'engage à promouvoir le développement de ces plantes uniques en collaboration étroite avec de petits producteurs.<br />
INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (46, 'qualite', 'qui_sommes_nous', 'Qualité', 'Quality');
INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (47, 'qualite_desc', 'qui_sommes_nous', 'Tous nos produits sont purs et 100 % naturels, issus de plantes cultivées de manière biologique et écologique. Chaque produit fait l\'objet d\'analyses rigoureuses afin de garantir sa qualité et son authenticité.', 'All our products are pure and 100% natural, sourced from organically and ecologically grown plants. Each product undergoes thorough testing to ensure quality and its authenticity.');
