INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (50, 'filiere_desc', 'qui_sommes_nous', 'Nous structurons des associations et coopératives paysannes en filières agricoles.', 'We structure Farmer associations and cooperatives into agricultural sectors.');
INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (51, 'approche', 'qui_sommes_nous', 'Approche', 'Approach');
INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (52, 'approche_desc', 'qui_sommes_nous', 'MAD\'AROM s\'engage à respecter la nature et l\'environnement en proposant des solutions pour améliorer les conditions de travail ainsi que la gestion de l\'énergie, de l\'eau et des déchets.', 'MAD\'AROM is committed to respecting nature and the environment by offering solutions to improve working conditions, as well as energy, water, and waste management.');
INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (53, 'title', 'qui_sommes_nous', 'Qui sommes-nous?', 'Who are we?');
