INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (61, 'approche', 'nos_activite', 'Approche', 'Approach');
INSERT INTO madarom.i18n (id, `key`, page, fr, en) VALUES (62, 'approche_desc', 'nos_activite', 'MAD\'AROM s\'engage en faveur de la durabilité en améliorant continuellement les conditions de travail, l\'efficacité énergétique, la conservation de l\'eau et la gestion des déchets. Nous offrons également une formation et un soutien technique tout au long du processus, de la plantation à la distillation, garantissant ainsi que nos produits finis de haute qualité répondent aux normes les plus élevées.', 'MAD\'AROM is committed to sustainability by continuously improving working conditions, energy efficiency, water conservation, and waste management. We also provide training and technical support throughout the entire process, from planting to distillation, ensuring that our high-quality finished products meet the highest standards.');
